settings.custom_liquid:
{% comment %}
Brand with link to collection with same name
{% endcomment %}

<p class="product__text caption-with-letter-spacing rte" {{ block.shopify_attributes }}>

{% capture my_collection_handle %}
  {{ product.vendor | handleize | strip | escape }}
{% endcapture %}

{% assign my_collection_handle_stripped = my_collection_handle | strip | escape %}

{% for collection in collections %}
  {% if my_collection_handle_stripped == collection.handle %}
    {% assign producer_image = collection.image %}
    {% assign producer_description = collection.description %}
    {% assign its_a_match = true %}
  {% endif %}
{% endfor %}

{% if its_a_match %}
  <a href="/collections/{{ my_collection_handle_stripped | handleize }}">{{ product.vendor }}</a>
{% else %}
  {{ product.vendor | link_to_vendor }}</span>
{% endif %}

</p>

{% comment %}
Zip Pay and Afterpay 
{% endcomment %}

{% capture var %}{% render 'locksmith-variables', variable: 'access_granted', scope: 'subject', subject: product %}{% endcapture %}{% if var == 'true' %}{% assign locksmith_access_granted = true %}{% else %}{% assign locksmith_access_granted = false %}{% endif %}


{% if locksmith_access_granted %}
<!-- Zip WIDGET -->
<div class="zip-product">
{%render 'zip-widget'%}
<span style="cursor:pointer;" id="shopify-zip-prod-widget" data-zm-asset="productwidget" data-zm-widget="popup" data-zm-popup-asset="termsdialog"></span>
</div>
<!-- end Zip WIDGET -->

<div class="afterpay-outer">
<div class="afterpay-inner">
</div>
</div>
{% endif %}

{% comment %}
Rerigeration and Bonus
{% endcomment %}

{% if product.tags contains 'refrigeration required' %}

<div class="productouter productrefrigeration">
<div class="productinner">

<img src="{{ 'icon-refrigeration-required.png' | file_url }}">

<h2>Requires Refrigeration</h2>

<p>This product requires refrigeration. 
Due to the heat sensitive nature of this product, <strong>EXPRESS SHIPPING</strong> must be selected at checkout. <a href="../pages/frequently-asked-questions">Read more</a></p>

</div>
</div>

<div style="clear: both;"> </div>
{% endif %}


{% if product.metafields.product.bonus != blank %}

<div class="productouter productbonus">
<div class="productinner">
<img src="{{ 'bonus.png' | file_url }}">

{{ product.metafields.product.bonus }}

</div>
</div>

<div style="clear: both;"> </div>
{% endif %}

